mapper.select:
select board_no, title, content, writer, view_cnt, create_date 
from tbl_board 
where board_no = #{bno}

mapper.update:
	UPDATE TBL_BOARD
  SET    VIEW_CNT = VIEW_CNT + 1
  WHERE  BOARD_NO = #{bno}

	update tbl_board
  set    title= #{title},
         content = #{content}
  where  board_no = #{boardNo}
